#| viewerHeight: 550
#| components: [editor, viewer]


library(shiny)

# Workaround for Chromium Issue 468227 ######
# Need this to properly download the csv file
# this bug and workaround is only for shinylive, 
#you do not need it in your regular app
downloadButton <- function(...) {
  tag <- shiny::downloadButton(...)
  tag$attribs$download <- NULL
  tag
}
### End of workaround ####

## see the warning callout under this code chunk
Sys.setenv(
    RSTUDIO_PANDOC="/Applications/RStudio.app/Contents/Resources/app/quarto/bin/tools/aarch64")

## I got the same error message with another pandoc version at macOS system level
## Sys.setenv(RSTUDIO_PANDOC="/opt/homebrew/bin")

ui <- fluidPage(
  sliderInput("n", "Number of points", 1, 100, 50),
  downloadButton("report", "Generate report")
)

server <- function(input, output, session) {
  output$report <- downloadHandler(
    filename = "report.html",
    content = function(file) {
      params <- list(n = input$n)

      id <- showNotification(
        "Rendering report...",
        duration = NULL,
        closeButton = FALSE
      )
      on.exit(removeNotification(id), add = TRUE)

      rmarkdown::render("report.Rmd",
        output_file = file,
        params = params,
        envir = new.env(parent = globalenv())
      )
    }
  )
}

shinyApp(ui, server)


## file: report.Rmd
## {{< include report.Rmd >}}
```

